    private class ClickHandler implements EventHandler<MouseEvent> {

        @Override
        public void handle(MouseEvent event) {
            int x = (int)event.getX();
            int y = (int)event.getY();
            int i = x / tileSide;
            int j = y / tileSide;

            Cell cell = gameManager.getField().getCell(i, j);
            update(i, j, cell.isBomb() ? -1 : ((Number) cell).getValue());
        }
    }

    private void update(int i, int j, int value) {
        if (value == -1) {
            ImageView bomb = new ImageView(bombIcon);
            gridPane.add(bomb, i, j, 1,1);
            gameManager.gameOver();
        } else {
            ImageView number = pickNumber(value);
            gridPane.add(number, i, j, 1, 1);
        }
    }

    // TODO: finire (deve ritornare le icone adeguate)
    private ImageView pickNumber(int value) {
        switch (value) {
            case 0:
                return new ImageView(emptyTileIcon);
            case 1:
                return new ImageView();
            case 2:
                return new ImageView();
            case 3:
                return new ImageView();
            case 4:
                return new ImageView();
            case 5:
                return new ImageView();
            case 6:
                return new ImageView();
            case 7:
                return new ImageView();
            case 8:
                return new ImageView();
        }
        return null;
    }
